export default{
    product_list: (product, variedades = []) => {
        return {
            _id: product._id,
            title: product.title,
            slug: product.slug,
            sku: product.sku,
            imagen : 'http://localhost:3000'+'/api/products/upload/product/'+product.portada,
            categorie: product.categorie,
            price_usd: product.price_usd,
            price_mxn: product.price_mxn,
