            let _id = req.params.id;
            await models.Variedad.findByIdAndDelete({_id:_id});

            res.status(200).json({
                message: "SE ELEIMINO LA VARIEDAD"
            });
        }catch(error){
            res.status(500).send({
                message: "Ocurrio un problema"
            });
            console.log(error);
        }
    },
}

